import{_ as l,c as i,o as p,ae as o}from"./chunks/framework.Dh1jimFm.js";const u=JSON.parse('{"title":"计算机网络的分类","description":"","frontmatter":{},"headers":[],"relativePath":"web-development-basics/computer-network/概述/03.计算机网络的分类.md","filePath":"web-development-basics/computer-network/概述/03.计算机网络的分类.md","lastUpdated":1736255633000}'),t={name:"web-development-basics/computer-network/概述/03.计算机网络的分类.md"};function a(c,e,_,r,d,s){return p(),i("div",null,e[0]||(e[0]=[o('<h1 id="计算机网络的分类" tabindex="-1">计算机网络的分类 <a class="header-anchor" href="#计算机网络的分类" aria-label="Permalink to &quot;计算机网络的分类&quot;">​</a></h1><ol><li><p>按分布范围分类：</p><ul><li><p>广域网（WAN）：</p><p>范围：几十～几千公里</p></li><li><p>城域网（MAN）：</p><p>范围：几千米～几十千米</p><p>通信技术：采用“以太网”技术</p></li><li><p>局域网（LAN）：</p><p>范围：几十米～几千米</p><p>通信技术：以太网技术</p></li><li><p>个域网（PAN）：</p><p>范围：几十米以内</p><p>通信技术：通过无线技术将个人设备连接起来的网络，因此也称<code>无线个域网</code>(WPAN)</p></li></ul></li><li><p>按传输技术分类：</p><ul><li>广播式网络：当一台计算机发送数据分组时，广播范围内所有计算机都会收到该分组，并通过检查分组的目的地址决定是否接受该分组</li><li>点对点式网络： 数据只会从发送方“点对点”发到接受方，精准送达</li></ul></li><li><p>按拓补结构分类：</p><ul><li>总线型结构：数据“广播式”传输，存在总线争用问题，典型代表：集线器连接的设备</li><li>环形结构：数据“广播式”传输，通过<code>令牌</code>解决总线争用问题，令牌顺环形依次传递，拿到令牌者可使用总线</li><li>星型结构：由中央设备实现数据的<code>点对点</code>传输，不存在<code>总线专用</code>的问题</li><li>网状结构：数据通过各中间节点逐一存储转发，属于<code>点到点</code>传输。</li></ul></li><li><p>按使用者分类：</p><ul><li>公用网：向公众开放的网络。</li><li>专用网：仅供某个组织内部使用的网络。</li></ul></li><li><p>按传输介质分类：有线网、无线网</p></li></ol>',2)]))}const m=l(t,[["render",a]]);export{u as __pageData,m as default};
