import{_ as t,c as a,o,ae as r}from"./chunks/framework.Dh1jimFm.js";const m=JSON.parse('{"title":"物理层基本概念","description":"","frontmatter":{},"headers":[],"relativePath":"web-development-basics/computer-network/物理层/01.基本概念.md","filePath":"web-development-basics/computer-network/物理层/01.基本概念.md","lastUpdated":1736255633000}'),l={name:"web-development-basics/computer-network/物理层/01.基本概念.md"};function _(i,e,s,n,c,p){return o(),a("div",null,e[0]||(e[0]=[r('<h1 id="物理层基本概念" tabindex="-1">物理层基本概念 <a class="header-anchor" href="#物理层基本概念" aria-label="Permalink to &quot;物理层基本概念&quot;">​</a></h1><p>物理层解决如何在连接各种计算机的传输媒体上传输比特流数据，而不是指具体的传输媒体。</p><p>物理层主要任务：确定与传输媒体接口有关的一些特性。（定义标准）</p><ol><li>机械特性：定义物理连接的特性，规定物理连接时所采用的规格、接口形状、引线数量和排列情况。</li><li>电器特性：规定传输二进制时，线路上信号的电压范围，阻抗匹配、传输速率和距离限制等。</li><li>功能特性：指明某条线上出现某一<code>电平表示何种意义</code>，接口部分的信号线的用途。</li><li>过程特性：过程特性定义各条物理线路的工作规程和时序关系。</li></ol>',4)]))}const u=t(l,[["render",_]]);export{m as __pageData,u as default};
