import{_ as e,c as l,o as d,ae as a}from"./chunks/framework.Dh1jimFm.js";const o=JSON.parse('{"title":"HTTP响应头信息","description":"","frontmatter":{},"headers":[],"relativePath":"web-development-basics/http/05.HTTP响应头信息.md","filePath":"web-development-basics/http/05.HTTP响应头信息.md","lastUpdated":1734360942000}'),n={name:"web-development-basics/http/05.HTTP响应头信息.md"};function i(s,t,r,f,g,x){return d(),l("div",null,t[0]||(t[0]=[a('<h1 id="http响应头信息" tabindex="-1">HTTP响应头信息 <a class="header-anchor" href="#http响应头信息" aria-label="Permalink to &quot;HTTP响应头信息&quot;">​</a></h1><p>HTTP 响应头信息是服务器在响应客户端的HTTP请求时发送的一系列头字段，他们提供了关于响应的附加信息和服务器的指令。</p><table tabindex="0"><thead><tr><th style="text-align:left;">响应头信息（英文）</th><th style="text-align:left;">响应头信息（中文）</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:left;">Date</td><td style="text-align:left;">日期</td><td style="text-align:left;">响应生成的日期和时间。例如：Wed, 18 Apr 2024 12:00:00 GMT</td></tr><tr><td style="text-align:left;">Server</td><td style="text-align:left;">服务器</td><td style="text-align:left;">服务器软件的名称和版本。例如：Apache/2.4.1 (Unix)</td></tr><tr><td style="text-align:left;">Content-Type</td><td style="text-align:left;">内容类型</td><td style="text-align:left;">响应体的媒体类型（MIME类型），如<code>text/html; charset=UTF-8</code>, <code>application/json</code>等。</td></tr><tr><td style="text-align:left;">Content-Length</td><td style="text-align:left;">内容长度</td><td style="text-align:left;">响应体的大小，单位是字节。例如：3145</td></tr><tr><td style="text-align:left;">Content-Encoding</td><td style="text-align:left;">内容编码</td><td style="text-align:left;">响应体的压缩编码，如 <code>gzip</code>, <code>deflate</code>等。</td></tr><tr><td style="text-align:left;">Content-Language</td><td style="text-align:left;">内容语言</td><td style="text-align:left;">响应体的语言。例如：zh-CN</td></tr><tr><td style="text-align:left;">Content-Location</td><td style="text-align:left;">内容位置</td><td style="text-align:left;">响应体的 URI。例如：/index.html</td></tr><tr><td style="text-align:left;">Content-Range</td><td style="text-align:left;">内容范围</td><td style="text-align:left;">响应体的字节范围，用于分块传输。例如：bytes 0-999/8000</td></tr><tr><td style="text-align:left;">Cache-Control</td><td style="text-align:left;">缓存控制</td><td style="text-align:left;">控制响应的缓存行为, 如 no-cache 表示必须重新请求。</td></tr><tr><td style="text-align:left;">Connection</td><td style="text-align:left;">连接</td><td style="text-align:left;">管理连接的选项，如<code>keep-alive</code>或<code>close</code>，keep-alive 表示连接不会在传输后关闭。。</td></tr><tr><td style="text-align:left;">Set-Cookie</td><td style="text-align:left;">设置 Cookie</td><td style="text-align:left;">设置客户端的 cookie。例如：sessionId=abc123; Path=/; Secure</td></tr><tr><td style="text-align:left;">Expires</td><td style="text-align:left;">过期时间</td><td style="text-align:left;">响应体的过期日期和时间。例如：Thu, 18 Apr 2024 12:00:00 GMT</td></tr><tr><td style="text-align:left;">Last-Modified</td><td style="text-align:left;">最后修改时间</td><td style="text-align:left;">资源最后被修改的日期和时间。例如：Wed, 18 Apr 2024 11:00:00 GMT</td></tr><tr><td style="text-align:left;">ETag</td><td style="text-align:left;">实体标签</td><td style="text-align:left;">资源的特定版本的标识符。例如：&quot;33a64df551425fcc55e6&quot;</td></tr><tr><td style="text-align:left;">Location</td><td style="text-align:left;">位置</td><td style="text-align:left;">用于重定向的 URI。例如：/newresource</td></tr><tr><td style="text-align:left;">Pragma</td><td style="text-align:left;">实现特定的指令</td><td style="text-align:left;">包含实现特定的指令，如 <code>no-cache</code>。</td></tr><tr><td style="text-align:left;">WWW-Authenticate</td><td style="text-align:left;">认证信息</td><td style="text-align:left;">认证信息，通常用于HTTP认证。例如：Basic realm=&quot;Access to the site&quot;</td></tr><tr><td style="text-align:left;">Accept-Ranges</td><td style="text-align:left;">接受范围</td><td style="text-align:left;">指定可接受的请求范围类型。例如：bytes</td></tr><tr><td style="text-align:left;">Age</td><td style="text-align:left;">经过时间</td><td style="text-align:left;">响应生成后经过的秒数，从原始服务器生成到代理服务器。例如：24</td></tr><tr><td style="text-align:left;">Allow</td><td style="text-align:left;">允许方法</td><td style="text-align:left;">列出资源允许的 HTTP 方法 。例如：GET, POST，HEAD等</td></tr><tr><td style="text-align:left;">Vary</td><td style="text-align:left;">变化</td><td style="text-align:left;">告诉下游代理如何使用响应头信息来确定响应是否可以从缓存中获取。例如：Accept</td></tr><tr><td style="text-align:left;">Strict-Transport-Security</td><td style="text-align:left;">严格传输安全</td><td style="text-align:left;">指示浏览器仅通过 HTTPS 与服务器通信。例如：max-age=31536000; includeSubDomains</td></tr><tr><td style="text-align:left;">X-Frame-Options</td><td style="text-align:left;">框架选项</td><td style="text-align:left;">控制页面是否允许在框架中显示，防止点击劫持攻击。例如：SAMEORIGIN</td></tr><tr><td style="text-align:left;">X-Content-Type-Options</td><td style="text-align:left;">内容类型选项</td><td style="text-align:left;">指示浏览器不要尝试猜测资源的 MIME 类型。例如：nosniff</td></tr><tr><td style="text-align:left;">X-XSS-Protection</td><td style="text-align:left;">XSS保护</td><td style="text-align:left;">控制浏览器的 XSS 过滤和阻断。例如：1; mode=block</td></tr><tr><td style="text-align:left;">Public-Key-Pins</td><td style="text-align:left;">公钥固定</td><td style="text-align:left;">HTTP 头信息，用于HTTP公共密钥固定（HPKP），一种安全机制，用于防止中间人攻击。例如：pin-sha256=&quot;base64+primarykey&quot;; pin-sha256=&quot;base64+backupkey&quot;; max-age=expireTime</td></tr></tbody></table>',3)]))}const c=e(n,[["render",i]]);export{o as __pageData,c as default};
