import{_ as i,c as e,a0 as a,o}from"./chunks/framework.CoVXEd1Z.js";const t="/assets/Computer-components.Cyb06eRG.png",m=JSON.parse('{"title":"互联网的概述","description":"","frontmatter":{},"headers":[],"relativePath":"web-development-basics/computer-network/概述/01.互联网的概述.md","filePath":"web-development-basics/computer-network/概述/01.互联网的概述.md","lastUpdated":1736416014000}'),r={name:"web-development-basics/computer-network/概述/01.互联网的概述.md"};function n(p,l,s,c,d,u){return o(),e("div",null,l[0]||(l[0]=[a('<h1 id="互联网的概述" tabindex="-1">互联网的概述 <a class="header-anchor" href="#互联网的概述" aria-label="Permalink to &quot;互联网的概述&quot;">​</a></h1><h2 id="什么是计算机网络" tabindex="-1">什么是计算机网络？ <a class="header-anchor" href="#什么是计算机网络" aria-label="Permalink to &quot;什么是计算机网络？&quot;">​</a></h2><p>计算机网络是一个将众多分散的、自治的计算机系统，通过通信设备与线路连接起来，由功能完善的软件实现资源共享和信息传递的系统。</p><p>与网络相连接的计算机称为主机，在互联网中的路由器，是一种特殊的计算机（有CPU,存储器，操作系统）等，但不能称为主机。</p><h2 id="计算机网络、互连网、互联网之间的区别" tabindex="-1">计算机网络、互连网、互联网之间的区别 <a class="header-anchor" href="#计算机网络、互连网、互联网之间的区别" aria-label="Permalink to &quot;计算机网络、互连网、互联网之间的区别&quot;">​</a></h2><ol><li><p><strong>计算机网络</strong>：</p><p>由若干个<code>节点</code>和连接这些节点的<code>链路</code>组成。</p><p>节点：可以是计算机、集线器、交换机、路由器等。</p><p>链路：可以是有线链路和无线链路</p></li><li><p><strong>互连网</strong>：</p><p>通过路由器把两个或多个计算机网络互相连接起来，形成规模较大更大的计算机网络，称为“互连网”。</p></li><li><p><strong>互联网</strong>：</p><p>由各大ISP和国际机构组建的，覆盖全球范围的互连网。（特指全球最大的计算机网络）</p><p>互联网必须使用<code>TCP/IP</code>协议进行通信，互连网可以使用任意协议通信。</p></li></ol><h2 id="计算机网络的组成和功能" tabindex="-1">计算机网络的组成和功能 <a class="header-anchor" href="#计算机网络的组成和功能" aria-label="Permalink to &quot;计算机网络的组成和功能&quot;">​</a></h2><h4 id="组成" tabindex="-1">组成 <a class="header-anchor" href="#组成" aria-label="Permalink to &quot;组成&quot;">​</a></h4><ul><li><p><strong>从组成部分看</strong>：</p><ol><li>硬件： <ul><li>主机（与网络相连接的计算机称为主机），也称<code>端系统</code>：如电脑，手机物联网设备等</li><li>通信设备：如集线器、交换机、路由器</li><li>通信线路：如光纤、网线等。</li></ul></li><li>软件：实现数据交换的通信软件</li><li>协议： <ul><li>规定计算机网络中的通信规则</li><li>由硬件、软件共同实现，如：网络适配器（网卡）+软件 实现网络通信协议</li></ul></li></ol></li><li><p><strong>从工作方式看</strong>：</p><ol><li><p>边缘部分：</p><ul><li>工作方式：直接为以后服务</li><li>主要由连接到互联网上的主机及其软件组成</li></ul></li><li><p>核心部分</p><ul><li>工作方式：为边缘部分提供服务（连通性，交换服务：动态的选择数据在网络当中传送的路径，保证网络中传输路径的负载不会太重）</li><li>由大量网络和连接这些网络的路由器组成</li></ul><p><img src="'+t+'" alt="Computer-components"></p></li></ol></li><li><p><strong>从逻辑功能上看</strong>：</p><ol><li>资源子网： <ul><li>计算机网络中运行应用程序，向用户提供可共享的硬件、软件和信息资源的部分</li><li>主要由连接到互联网上的主机组成</li></ul></li><li>通信子网 <ul><li>计算机网络中负责计算机信息传输的部分</li><li>主要由通信链路+ 通信设备 + 协议 构成</li><li>主机内部实现信息传输的网络适配器、底层协议 属于通信子网的范畴</li></ul></li></ol></li></ul><h4 id="功能" tabindex="-1">功能： <a class="header-anchor" href="#功能" aria-label="Permalink to &quot;功能：&quot;">​</a></h4><ol><li>数据通信：实现计算机之间的数据传输。是最基本、最重要的功能</li><li>资源共享：硬件（智能音箱 请求服务器处理，服务器属于硬件资源）、软件（软件应用商店）、数据资源（分享视频）</li><li>分布式处理：将某个复杂任务分配给网络中的多台计算机处理</li><li>提高可靠性：网络中各台计算机互为替代机</li><li>负载均衡：网络中各台计算机共同分担繁重工作</li></ol>',11)]))}const b=i(r,[["render",n]]);export{m as __pageData,b as default};
