import{_ as e,c as i,a0 as o,o as l}from"./chunks/framework.CoVXEd1Z.js";const T=JSON.parse('{"title":"HTTP Content-Type","description":"","frontmatter":{},"headers":[],"relativePath":"web-development-basics/http/07.HTTP Content-Type.md","filePath":"web-development-basics/http/07.HTTP Content-Type.md","lastUpdated":1734360942000}'),p={name:"web-development-basics/http/07.HTTP Content-Type.md"};function a(n,t,c,d,r,m){return l(),i("div",null,t[0]||(t[0]=[o('<h1 id="http-content-type" tabindex="-1">HTTP Content-Type <a class="header-anchor" href="#http-content-type" aria-label="Permalink to &quot;HTTP Content-Type&quot;">​</a></h1><p><code>Content-Type</code>（内容类型），一般是指网页中存在的 <code>Content-Type</code>，用于定义网络文件的类型和网页的编码，决定浏览器以什么形式，什么编码读取这个文件，这就是经常看见一些<code>PHP</code>网页点击的结果却是下载一个文件或者一张图片的原因。</p><p><code>Content-Type</code> 标头告诉客户端实际返回的内容的<strong>类型</strong>。</p><p>常见的媒体格式类型：</p><ul><li>text/html ： HTML格式</li><li>text/plain ：纯文本格式</li><li>text/xml ： XML格式</li><li>image/gif ：gif图片格式</li><li>image/jpeg ：jpg图片格式</li><li>image/png：png图片格式</li></ul><p>以<code>application</code>开头的媒体格式类型：</p><ul><li>application/xhtml+xml ：XHTML格式</li><li>application/xml： XML数据格式</li><li>application/atom+xml ：Atom XML聚合格式</li><li>application/json： JSON数据格式</li><li>application/pdf：pdf格式</li><li>application/msword ： Word文档格式</li><li>application/octet-stream ： 二进制流数据（如常见的文件下载）</li><li>application/x-www-form-urlencoded ： <code>form</code>表单中默认的encType，form表单数据被编码为key/value格式发送到服务器（表单默认的提交数据的格式）</li><li>multipart/form-data ： 需要在表单中进行文件上传时，就需要使用该格式</li></ul>',7)]))}const f=e(p,[["render",a]]);export{T as __pageData,f as default};
